using Network.Server.Node.Core;
using Proto.Test;

namespace NetworkEngine.Tests.Node.Controller
{
    [NodeController]
    public class TestController(ILogger<TestController> logger)
    {
        [PacketHandler(EchoReq.MsgId)]
        public Task<EchoRes> EchoHandler(EchoReq req)
        {
            logger.LogWarning($"Echo req: {req}");

            return Task.FromResult(new EchoRes
            {
                Message = req.Message + "_" + "ANSWER",
                Timestamp = 0
            });
        }
        
        [PacketHandler(Echo2Req.MsgId)]
        public Task<Echo2Res> Echo2Handler(Echo2Req req)
        {
            logger.LogWarning($"Echo req: {req}");

            return Task.FromResult(new Echo2Res
            {
                Message = req.Message + "_" + "ANSWER",
                Timestamp = 0
            });
        }
    }

